package com.gift.bean;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Repository;/** * @Repository @Service @Controller，它们分别对应存储层Bean，业务层Bean，和展示层Bean。 * @Component泛指组件，当组件不好归类的时候，我们可以使用这个注解进行标注。 * @scope="prototype"来保证每一个请求有一个单独的Action来处理， 避免struts中Action的线程安全问题。 */@Repository@Scope("prototype")public class Gift_code implements java.io.Serializable {	private static final long serialVersionUID = 1L;	private Integer pk_id;	private String name;	private String code;	private String parent_code;	private Integer create_time;	private Integer add_user_id;	public Integer getPk_id() {		return pk_id;	}	public void setPk_id(Integer pk_id) {		this.pk_id = pk_id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getCode() {		return code;	}	public void setCode(String code) {		this.code = code;	}	public String getParent_code() {		return parent_code;	}	public void setParent_code(String parent_code) {		this.parent_code = parent_code;	}	public Integer getCreate_time() {		return create_time;	}	public void setCreate_time(Integer create_time) {		this.create_time = create_time;	}	public Integer getAdd_user_id() {		return add_user_id;	}	public void setAdd_user_id(Integer add_user_id) {		this.add_user_id = add_user_id;	}}