package com.gift.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.gift.bean.Gift_cangku;import com.gift.dao.Gift_cangkuDAO;import com.gift.service.Gift_cangkuService;/** * @Repository @Service @Controller，它们分别对应存储层Bean，业务层Bean，和展示层Bean。 * @Component泛指组件，当组件不好归类的时候，我们可以使用这个注解进行标注。 * @scope="prototype"来保证每一个请求有一个单独的Action来处理， 避免struts中Action的线程安全问题。 */@Servicepublic class Gift_cangkuServiceImpl implements Gift_cangkuService {	private Gift_cangkuDAO gift_cangkuDao;	public Gift_cangkuDAO getGift_cangkuDao() {		return gift_cangkuDao;	}	@Resource(name = "gift_cangkuDAOImpl")	public void setGift_cangkuDao(Gift_cangkuDAO gift_cangkuDao) {		this.gift_cangkuDao = gift_cangkuDao;	}	public List<Gift_cangku> findAll(int pageId, int pageSize) {		return this.gift_cangkuDao.findAllGift_cangku(pageId, pageSize);	}	public Integer findAll_count() {		return this.gift_cangkuDao.findAllGift_cangku_count();	}	public Gift_cangku findById(Integer ck_id) {		return this.gift_cangkuDao.findGift_cangkuById(ck_id);	}	public Gift_cangku findByItemNo(String item_no) {		return this.gift_cangkuDao.findGift_cangkuByItemNo(item_no);	}		public List<Gift_cangku> findByKeyword(Map searchMap, int pageId, int pageSize) {		return this.gift_cangkuDao.findGift_cangkuByKeyword(searchMap, pageId, pageSize);	}	public Integer findByKeyword_count(Map searchMap) {		return this.gift_cangkuDao.findGift_cangkuByKeyword_count(searchMap);	}	public void update(Gift_cangku gift_cangku) {		this.gift_cangkuDao.updateGift_cangku(gift_cangku);	}	public int save(Gift_cangku gift_cangku) {		return this.gift_cangkuDao.saveGift_cangku(gift_cangku);	}		public int deleteByItemNo(Gift_cangku gift_cangku) {		return this.gift_cangkuDao.removeGift_cangkuByItemNo(gift_cangku);	}	public Integer deleteById(Integer ck_id) {		return this.gift_cangkuDao.removeGift_cangkuById(ck_id);	}}