package com.gift.service.impl;import java.util.List;import javax.annotation.Resource;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.gift.bean.Gift_fujian;import com.gift.dao.Gift_fujianDAO;import com.gift.service.Gift_fujianService;/** * @Repository @Service @Controller，它们分别对应存储层Bean，业务层Bean，和展示层Bean。 * @Component泛指组件，当组件不好归类的时候，我们可以使用这个注解进行标注。 * @scope="prototype"来保证每一个请求有一个单独的Action来处理， 避免struts中Action的线程安全问题。 */@Servicepublic class Gift_fujianServiceImpl implements Gift_fujianService {	private Gift_fujianDAO giftFujianDao;	public Gift_fujianDAO getGiftFujianDao() {		return giftFujianDao;	}	@Resource(name = "gift_fujianDAOImpl")	public void setGiftFujianDao(Gift_fujianDAO giftFujianDao) {		this.giftFujianDao = giftFujianDao;	}	public List<Gift_fujian> findAll() {		return this.giftFujianDao.findAllGift_fujian();	}	public List<Gift_fujian> findByItemNo(String item_no) {		return this.giftFujianDao.findGift_fujianListByGiftItemNo(item_no);	}		@Transactional	public void save(Gift_fujian giftFujian) {		this.giftFujianDao.saveGift_fujian(giftFujian);	}	@Transactional	public int deleteByItemNo(Gift_fujian giftFujian) {		return this.giftFujianDao.removeGift_fujianByItemNo(giftFujian);	}	@Transactional	public int deleteById(int fj_id) {		return this.giftFujianDao.removeGift_fujianById(fj_id);	}	public Gift_fujian findByID(Integer i_id) {		return this.giftFujianDao.findGift_fujianByID(i_id);	}	@Transactional	public void update(Gift_fujian giftFujian) {		this.giftFujianDao.updateGift_fujian(giftFujian);	}}