package com.gift.service.impl;import java.util.List;import javax.annotation.Resource;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.gift.bean.Gift_peoples;import com.gift.dao.Gift_peoplesDAO;import com.gift.service.Gift_peoplesService;/** * @Repository @Service @Controller，它们分别对应存储层Bean，业务层Bean，和展示层Bean。 * @Component泛指组件，当组件不好归类的时候，我们可以使用这个注解进行标注。 * @scope="prototype"来保证每一个请求有一个单独的Action来处理， 避免struts中Action的线程安全问题。 */@Servicepublic class Gift_peoplesServiceImpl implements Gift_peoplesService {	private Gift_peoplesDAO giftPeoplesDao;	public Gift_peoplesDAO getGiftPeoplesDao() {		return giftPeoplesDao;	}	@Resource(name = "gift_peoplesDAOImpl")	public void setGift_detailDao(Gift_peoplesDAO giftPeoplesDao) {		this.giftPeoplesDao = giftPeoplesDao;	}	@Transactional	public void save(Gift_peoples giftPeoples) throws Exception {		this.giftPeoplesDao.saveGiftPeoples(giftPeoples);	}	@Transactional	public void delete(Gift_peoples giftPeoples) throws Exception {		this.giftPeoplesDao.removeGiftPeoples(giftPeoples);	}	public Gift_peoples findByID(Integer i_id) throws Exception {		return this.giftPeoplesDao.findGiftPeoplesByID(i_id);	}	@Transactional	public void update(Gift_peoples giftPeoples) throws Exception {		this.giftPeoplesDao.updateGiftPeoples(giftPeoples);	}	@Override	public List<Gift_peoples> findByName(String p_name) throws Exception {		return this.giftPeoplesDao.findGiftPeoplesByName(p_name);	}	@Override	public void batchDelete(Integer[] batchId) throws Exception {		this.giftPeoplesDao.batchRemoveGiftPeople(batchId);	}	@Override	public int findByFlagCount(Integer p_flag) {		return this.giftPeoplesDao.findGiftPeoplesByFlagCount(p_flag);	}		@Override	public List<Gift_peoples> findByFlagPage(Integer p_flag,int i, int pageSize) {		return this.giftPeoplesDao.findGiftPeoplesByFlagPage(p_flag,i,pageSize);	}	@Override	public int findByKeyword_count(Integer p_flag,String keyword) {		return this.giftPeoplesDao.findGiftPeoplesByKeywordCount(p_flag,keyword);	}	@Override	public List<Gift_peoples> findByKeyword(Integer p_flag,String keyword, int pageId, int pageSize) {		return this.giftPeoplesDao.findGiftPeoplesByKeyword(p_flag,keyword, pageId, pageSize);	}	@Override	public List<Gift_peoples> findAll() {		return giftPeoplesDao.findAllGiftPeoples();	}	@Override	public List<Gift_peoples> findByFlag(int p_flag) {		return this.giftPeoplesDao.findGiftPeoplesByFlag(p_flag);	}	}