package com.gift.bean;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Repository;/** * @Repository @Service @Controller，它们分别对应存储层Bean，业务层Bean，和展示层Bean。 * @Component泛指组件，当组件不好归类的时候，我们可以使用这个注解进行标注。 * @scope="prototype"来保证每一个请求有一个单独的Action来处理， 避免struts中Action的线程安全问题。 */@Repository@Scope("prototype")public class Gift_group_operation implements java.io.Serializable {	private static final long serialVersionUID = 1L;	private Integer go_id;	private Integer group_id;	private Integer oper_id;	private Integer add_time;	private Integer add_user_id;	public Integer getGo_id() {		return go_id;	}	public void setGo_id(Integer go_id) {		this.go_id = go_id;	}	public Integer getGroup_id() {		return group_id;	}	public void setGroup_id(Integer group_id) {		this.group_id = group_id;	}	public Integer getOper_id() {		return oper_id;	}	public void setOper_id(Integer oper_id) {		this.oper_id = oper_id;	}	public Integer getAdd_time() {		return add_time;	}	public void setAdd_time(Integer add_time) {		this.add_time = add_time;	}	public Integer getAdd_user_id() {		return add_user_id;	}	public void setAdd_user_id(Integer add_user_id) {		this.add_user_id = add_user_id;	}}