package com.gift.bean;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Repository;/** * @Repository @Service @Controller，它们分别对应存储层Bean，业务层Bean，和展示层Bean。 * @Component泛指组件，当组件不好归类的时候，我们可以使用这个注解进行标注。 * @scope="prototype"来保证每一个请求有一个单独的Action来处理， 避免struts中Action的线程安全问题。 */@Repository@Scope("prototype")public class Gift_log implements java.io.Serializable {	private static final long serialVersionUID = 1L;		private Integer log_id;	private String log_content;	private String log_uri;	private String log_type;	private Integer add_time;	private Integer add_user_id;	public Integer getLog_id() {		return log_id;	}	public void setLog_id(Integer log_id) {		this.log_id = log_id;	}	public String getLog_content() {		return log_content;	}	public void setLog_content(String log_content) {		this.log_content = log_content;	}	public String getLog_uri() {		return log_uri;	}	public void setLog_uri(String log_uri) {		this.log_uri = log_uri;	}	public String getLog_type() {		return log_type;	}	public void setLog_type(String log_type) {		this.log_type = log_type;	}	public Integer getAdd_time() {		return add_time;	}	public void setAdd_time(Integer add_time) {		this.add_time = add_time;	}	public Integer getAdd_user_id() {		return add_user_id;	}	public void setAdd_user_id(Integer add_user_id) {		this.add_user_id = add_user_id;	}}