package com.gift.service.impl;import java.util.List;import javax.annotation.Resource;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.gift.bean.Gift_group;import com.gift.dao.Gift_groupDAO;import com.gift.service.Gift_groupService;/** * @Repository @Service @Controller，它们分别对应存储层Bean，业务层Bean，和展示层Bean。 * @Component泛指组件，当组件不好归类的时候，我们可以使用这个注解进行标注。 * @scope="prototype"来保证每一个请求有一个单独的Action来处理， 避免struts中Action的线程安全问题。 */@Servicepublic class Gift_groupServiceImpl implements Gift_groupService {	private Gift_groupDAO gift_groupDao;	public Gift_groupDAO getGift_groupDao() {		return gift_groupDao;	}	@Resource(name = "gift_groupDAOImpl")	public void setGift_groupDao(Gift_groupDAO gift_groupDao) {		this.gift_groupDao = gift_groupDao;	}	@Transactional	public void delete(Gift_group gift_group) throws Exception {		this.gift_groupDao.removeGift_group(gift_group);	}	public Gift_group findById(Integer group_id) throws Exception {		return this.gift_groupDao.findGift_groupByGroup_id(group_id);	}	@Transactional	public void save(Gift_group gift_group) throws Exception {		this.gift_groupDao.saveGift_group(gift_group);	}	@Transactional	public void update(Gift_group gift_group) throws Exception {		this.gift_groupDao.updateGift_group(gift_group);	}	@Override	public List<Gift_group> findByName(String group_name_c) throws Exception {		return gift_groupDao.findGift_groupByGroup_name(group_name_c);	}	@Override	public void batch_delete(Integer[] batchId) throws Exception {		gift_groupDao.batch_removeGift_group(batchId);	}	@Override	public int findByKeyword_count(String keyword) {		return gift_groupDao.findGift_groupByKeyword_count(keyword);	}	@Override	public List<Gift_group> findByKeyword(String keyword, int pageId, int pageSize) {		return gift_groupDao.findGift_groupByKeyword(keyword,pageId,pageSize);	}	@Override	public List<Gift_group> findAll() {		return this.gift_groupDao.findAllGift_group();	}	@Override	public int findAll_count() {		return this.gift_groupDao.findAllGift_group_count();	}}