package com.gift.service.impl;import java.util.List;import javax.annotation.Resource;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.gift.bean.Gift_group_user;import com.gift.bean.Gift_user;import com.gift.dao.Gift_group_userDAO;import com.gift.service.Gift_group_userService;/** * @Repository @Service @Controller，它们分别对应存储层Bean，业务层Bean，和展示层Bean。 * @Component泛指组件，当组件不好归类的时候，我们可以使用这个注解进行标注。 * @scope="prototype"来保证每一个请求有一个单独的Action来处理， 避免struts中Action的线程安全问题。 */@Servicepublic class Gift_group_userServiceImpl implements Gift_group_userService {	private Gift_group_userDAO gift_group_userDao;	public Gift_group_userDAO getGift_group_userDao() {		return gift_group_userDao;	}	@Resource(name = "gift_group_userDAOImpl")	public void setGift_group_userDao(Gift_group_userDAO gift_group_userDao) {		this.gift_group_userDao = gift_group_userDao;	}	@Transactional	public void delete(Gift_group_user gift_group_user) throws Exception {		this.gift_group_userDao.removeGift_group_user(gift_group_user);	}	@Transactional	public void save(Gift_group_user gift_group_user) throws Exception {		this.gift_group_userDao.saveGift_group_user(gift_group_user);	}	@Transactional	public void update(Gift_group_user gift_group_user) throws Exception {		this.gift_group_userDao.updateGift_group_user(gift_group_user);	}	@Override	public List<Gift_group_user> findAll(int i, int pageSize) {		return gift_group_userDao.findAllGift_group_user(i, pageSize);	}	@Override	public int findByKeyword_count(String keyword) {		return gift_group_userDao.findGift_group_userByKeyword_count(keyword);	}	@Override	public List<Gift_group_user> findByKeyword(String keyword, int pageId,			int pageSize) {		return gift_group_userDao.findGift_group_userByKeyword(keyword,pageId,pageSize);	}	@Override	public Gift_group_user findById(Integer gu_id) throws Exception {		return gift_group_userDao.findGift_group_userById(gu_id);	}	@Override	public int findAll_count() {		return gift_group_userDao.findAllGift_group_user_count();	}	@Override	public List<Gift_group_user> findByUser(Gift_user gift_user)			throws Exception {		return gift_group_userDao.findGift_group_userByUser(gift_user);	}}